import { useState } from "react";
import "./App.css";

function App() {
  const [value, setValue] = useState("");
  function handleValue(e) {
    setValue(+e.target.value);
  }
  // console.log(value);

  return (
    <div>
      <First value={value} handleValue={handleValue} />
      <Second value={value} />
      <Third value={value} />
      <Fouth value={value} />
      <Five value={value} />
    </div>
  );
}

function First({ value, handleValue }) {
  return (
    <div>
      <span> How much was the Bill ?</span>
      <input placeholder="cost of the food" onChange={handleValue} />
      <br />
    </div>
  );
}
function Second({ value }) {
  const [Second, setSecond] = useState(0);
  function SecondHanle(e) {
    setSecond(+e.target.value);
  }
  const math = (value * Second) / 100;
  console.log(math);
  return (
    <>
      <span> How did you like the service ? </span>
      <select onChange={SecondHanle}>
        <option value={0}>Dissatisfied (0%) </option>
        <option value={5}>It was okay (5%) </option>
        <option value={10}> It was good (10%) </option>
        <option value={20}> it was amazing (20%) </option>
      </select>
    </>
  );
}
function Third() {
  return (
    <>
      <br />
      <span> How did your friend like the service ? </span>
      <select>
        <option value="dd"> Dissatisfied (0%)</option>
        <option value="dd">It was okay (5%) </option>
        <option value="dd"> It was good (10%) </option>
        <option value="dd"> Absolutely amazing! (20%) </option>
      </select>
    </>
  );
}

function Fouth({ value }) {
  return (
    <h1>
      {" "}
      You pay $ {value === "" ? 0 : value} (${value === "" ? 0 : value} + $0
      tip)
    </h1>
  );
}
function Five() {
  return <button> Reset</button>;
}
export default App;
