import { useState } from "react";
import "./App.css";

function App() {
  const [value, setValue] = useState("");
  function handleValue(e) {
    setValue(+e.target.value);
  }

  const [Second, setSecond] = useState(0);
  function SecondHanle(e) {
    setSecond(+e.target.value);
  }
  const math = (value * Second) / 100;
  console.log(math);

  // const [Thirdst, setThirdst] = useState(0);
  // function thirdHanle(e) {
  //   setThirdst(+e.target.value);
  // console.log(Thirdst);
  // }

  return (
    <div>
      <First value={value} handleValue={handleValue} thirdHanle={thirdHanle} />
      <SecondF
        value={value}
        SecondHanle={SecondHanle}
        math={math}
        thirdHanle={thirdHanle}
      />
      <Third
        value={value}
        SecondHanle={SecondHanle}
        math={math}
        thirdHanle={thirdHanle}
      />
      <Fouth
        value={value}
        SecondHanle={SecondHanle}
        math={math}
        thirdHanle={thirdHanle}
      />
      <Five
        value={value}
        SecondHanle={SecondHanle}
        math={math}
        thirdHanle={thirdHanle}
      />
    </div>
  );
}

function First({ value, handleValue, SecondHanle, math }) {
  return (
    <div>
      <span> How much was the Bill ?</span>
      <input placeholder="cost of the food" onChange={handleValue} />
      <br />
    </div>
  );
}
function SecondF({ value, SecondHanle, math }) {
  return (
    <>
      <span> How did you like the service ? </span>
      <select onChange={SecondHanle}>
        <option value={0}>Dissatisfied (0%) </option>
        <option value={5}>It was okay (5%) </option>
        <option value={10}> It was good (10%) </option>
        <option value={20}> it was amazing (20%) </option>
      </select>
    </>
  );
}
function Third(math, handleValue, SecondHanle, thirdHanle) {
  return (
    <>
      <br />
      <span> How did your friend like the service ? </span>
      <select onChange={SecondHanle}>
        <option value={0}> Dissatisfied (0%)</option>
        <option value={5}>It was okay (5%) </option>
        <option value={10}> It was good (10%) </option>
        <option value={20}> Absolutely amazing! (20%) </option>
      </select>
    </>
  );
}

function Fouth({ value, math }) {
  console.log(math);
  return (
    <h1>
      {" "}
      You pay $ {value === "" ? 0 : value + math} (${value === "" ? 0 : value} +
      $ {math}
      tip)
    </h1>
  );
}
function Five() {
  return <button> Reset</button>;
}
export default App;
